import React, { useState } from "react";
import './ToDoList.css'
import AddIcon from '@material-ui/icons/Add';
import DelIcons from '@material-ui/icons/Delete';
import Button from '@material-ui/core/Button'
// import './Project8.css'
import  {Tooltip}  from "@material-ui/core";
import List from "./List";
const ToDoList = (()=>{

    const [output,setOut] = useState("")
    const [item,setItem] = useState([])

    const added = ((values)=>{
        setOut(values.target.value)
    })
    const add = (()=>{
        setItem((olditems)=>{
            return [...olditems,output]
        })
        
    })

    const deleteItem =((id)=>{
        console.log("Delted")
        setItem((olditems)=>{
            return olditems.filter((arrele,index)=>{
                return index != id 
            })
        })
    })
    return(
        <>
            <div className="container">

                <div className="heading"><h1>ToDo List</h1></div>
                <input className="inp" placeholder="Add a item" onChange={added}/>
                <Button className="btn" onClick={add}  ><AddIcon/></Button>

                <ol className="output">
                    {item.map((val,index)=>{
                        return <List
                            text = {val}
                            id = {index}
                            key = {index}
                            onSelect = {deleteItem}
                        />
                    })}
                </ol>

            </div>
        </>
    )
})
export default ToDoList
