import React, { useState } from "react";


const App = (()=>{

    
    let bg = 'rgb(237, 255, 255)'
    let c = 0
    const [bgc,setcol] = useState(bg)
    const bgChange=(()=>{
        
        if(c%2 == 0){
            bg = 'rgb(192, 221, 197)'
        }
        else{
            bg = 'rgb(237, 255, 255)'  
        }
        setcol(bg)
    })

    // const oribg = (()=>{
    //     bg = 'rgb(237, 255, 255)'
    //     setcol(bg)
    // })
    // setInterval(oribg,5000)


    return (
    <>
    <div className="divi" style={{backgroundColor: bgc}} >
        <button className="but" onClick={bgChange}>Click me</button>
    </div>
    </>
    )
})

export default App
