let Favs = ((val)=>{

    if(val.ptn == i)
    // return(<Cards key={val.id} nam = {val.nam} imsSrc = {srcc} ptn ={val.ptn} />)

    return (val.ptn === i ? <Cards key={val.id} nam = {val.nam} imsSrc = {srcc} ptn ={val.ptn} /> :alert("Wrong Input"))

})
