import './form.css'
import { getElementError } from "@testing-library/react";


const Forms = (()=>{


    const [full,setText] = useState({
        fname : "",
        lname : ""
    })
    // let inp = document.getElementById('inp').value
    // console.log(inp)

    const typed = ((val)=>{
        nam = val.target.value
    
    const values = val.target.value
    const name =  val.target.name  
    const greet = ((e)=>{
        e.preventDefault()
        setText( (values)=>{
            if(name =="fName"){
                values.fname = values.fname
                values.lname = values.lname
            }
            
        })
    })
})
    return (<>
        <div className="main">
        <form onSubmit={greet}>
            <h1 className="greet">Heloo, {full.fname} {full.lname}</h1>
            <input
            className="input"
            placeholder="Enter Your name" 
            onChange={typed}
            name = "fName"
            /><br/><br/>

            <input className="input" 
            placeholder="Enter Your last name"  
            onChange={typed} 
            name = "lNmae"
            /><br/>

            <button className="butt" type="submit">Submit üëç</button>
            </form>
        </div>
    </>)
})
export default Forms
