import React, { useState } from "react";
import AddIcon from '@material-ui/icons/Add';
import DelIcons from '@material-ui/icons/Delete';
import Button from '@material-ui/core/Button'
import './Project8.css' 
import  {Tooltip}  from "@material-ui/core";

const Project8 = (()=>{

    const[count,change] =useState(0)

    const inc = (()=>{
        change(count+1)
    })    
    const dec = (()=>{
        if(count >0){
        change(count-1)
        }
        else{
            alert("Minimum Vlaue of counter Reached ")
        }
    })


    return (<>
       <div className="main" width = "100%">
        <div className="card">
            <div className="heading"><h1>{count}</h1></div>

            <Tooltip title = "Add">
            <Button className="inc" onClick={inc}><AddIcon/></Button>
            </Tooltip>

            <Tooltip title = "delete">
            <Button className="dec" onClick={dec}><DelIcons/></Button>
            </Tooltip>
        </div>
       </div>
    </>)
})
export default Project8
