        setShow(true)
        setBr(false)
    })
    
    return (<>
        <div className="question"><Button onClick={showAns}>âž•</Button>{props.question}</div>{br && <br/>} 
        { show && <div className="answer">: {props.answer}</div> }
    </>)
})
