import React from 'react';
import Cards from './Cards' 
import Data from './data';
import Heading from './Heading';
import './index.css';

let favs = "Netflix"
let Favs =((val) =>{
    // if(val.comapny === favs)
    return((<Cards key={val.id} nam = {val.nam} imsSrc = {srcc} ptn ={val.ptn} />))
})
let srcc = "https://upload.wikimedia.org/wikipedia/commons/3/38/Stranger_Things_logo.png"
function App(){
    return (
    <>
        <div className="Conatiner">
        <Heading/>
        {Data.map(Favs)}
        </div>
        </>)
}
export default App
