 import React, { useState } from "react";
import './form.css'
import { getElementError } from "@testing-library/react";


const Forms = (()=>{
    let nam = ""
    let pas= ""
    const [name,setText] = useState("")
    const [pass,setpass] = useState("")
    // let inp = document.getElementById('inp').value
    // console.log(inp)


    const typed = ((val)=>{
        console.log(val.target.value)
        nam = val.target.value

    })
    // const typedp= ((val)=>{
    //     console.log(val.target.value)
    //     pas = val.target.value
    // })


    const greet = ((e)=>{
        e.preventDefault()
        setText(nam)
        setpass(pas)
    })
    return (<>
        <div className="main">
        <form onSubmit={greet}>
            <h1 className="greet">Heloo, {name} {pass}</h1>
            <input
            className="input"
            placeholder="Enter Your name" 
            onChange={typed}/><br/><br/>

            <input className="input" 
            placeholder="Enter Your last name"  
            onChange={typed} /><br/>
            
            <button className="butt" type="submit">Submit üëç</button>
            </form>
        </div>
    </>)
})
export default Forms
