import React from "react";
import ReactDOM from "react-dom";
import './index.css'

let heading = {
  color: '#fa9191',
  textAlign: 'center',
  textTransform: 'capitalize',
  fontWeight: 'bold'
}
let hour = new Date().getHours()
hour = 22
let greet = "null"
console.log(hour)


if(hour > 0 && hour < 12){
  greet = "Good Morning"
}
else if(hour >= 12 && hour < 18 ){
  greet = "Good Afternoon"
}
else{
  greet = "Good Night"
}

ReactDOM.render(
  <>
  <div className="Frame">
    <div className="Block">
      <div className="helo">Hello Sir,</div>
      <div className="time">{greet}</div>
    </div>
  </div>
  </>,
  document.getElementById("root")
)
