import React, { useState } from "react";
import './form.css'
import { getElementError } from "@testing-library/react";


const Forms = (()=>{


    const [full,setText] = useState({
        fname : "",
        lname : ""
    })

    const typed = ((val)=>{
    
    const value = val.target.value
    const name =  val.target.name 

    // set Text pass the object with previous value
    setText( (prevalues)=>{
        if(name =="fName"){
            return {
                fname :value,
                lname : prevalues.lname
            }
        }
        else if(name == "lName"){
            return {
                fname : prevalues.fname,
                lname :value
            }
        }
        
    })
})
const greet = ((e)=>{
    e.preventDefault()
})


    return (<>
        <div className="main">
        <form onSubmit={greet}>
            <h1 className="greet">Heloo, {full.fname} {full.lname}</h1>
            <input
            className="input"
            placeholder="Enter Your name" 
            onChange={typed}
            name = "fName"
            /><br/><br/>

            <input className="input" 
            placeholder="Enter Your last name"  
            onChange={typed} 
            name = "lName"
            /><br/>

            <button className="butt" type="submit">Submit üëç</button>
            </form>
        </div>
    </>)
})
export default Forms
