import React from "react";
import { useState } from "react";
import { Link } from 'react-router-dom';
import Button from '@material-ui/core/Button'
const Add = (()=>{

    const [book,setBooks] = useState({
            title : "",
            desc : "",
            cover : ""
        })
    const handelChange = ((e)=>{
       setBooks((prev)=>({...prev,[e.target.name]: e.target.value}))
    
    })
    console.log(book)
    return (
        <>
            <div className="form">
                <h1>Add New Book</h1>
                <input type="text" placeholder='title'  onChange={handelChange} name= "title"/><br/><br/>
                <input type="text" placeholder='desc'  onChange={handelChange} name= "desc"/><br/><br/>
                <input type="text" placeholder='cover' onChange={handelChange} name= "cover"/><br/><br/>

                <Button><Link to ="/add">add</Link></Button>
            </div>
        </>
    )
})
export default Add
