import React from "react";
import { useState } from "react";
const Add = (()=>{

    const [book,setBooks] = useState({
            title : "",
            desc : "",
            cover : ""
        })
    const handelChange = ((e)=>{
       setBooks((prev)=>({...prev,[e.target.name]: e.target.value}))
    })
    console.log(book)
    return (
        <>
            <div className="form">
                <h1>Add New Book</h1>
                <input type="text" placeholder='title'  onChange={handelChange}/><br/><br/>
                <input type="text" placeholder='desc'  onChange={handelChange}/><br/><br/>
                <input type="text" placeholder='cover' onChange={handelChange}/><br/><br/>

            </div>
        </>
    )
})
export default Add
