import React, { useEffect, useState } from "react";
import axios from "axios";
const Pokem = (()=>{
    const [num,setNum] = useState('ditto')

    useEffect(()=>{
        async function getData(){
            const res = await axios.get(`https://pokeapi.co/api/v2/pokemon/${num}`)
            console.log(res)
        }
        getData()
    })

    return (
        
        <>
            <h1>This is Pokemon number {num}</h1>
            <select value={num} onChange={(event)=>{
                setNum(event.target.value)
            }}>
                <option value = "ditto">1</option>
                <option value = "ditto">25</option>
                <option value = "ditto">3</option>
                <option value = "ditto">4</option>
            </select>
        </>
    )
})
export default Pokem
