import React from "react";
import ReactDOM from "react-dom";
import './index.css'

let Frame ={
  width: '1450px',  
  height: '1079px',
  borderRadius: '70px',
  background: '#B6ECBB'
}
let time = { 
  color: '#E4AE47',
  fontFamily: 'Inter',
  fontSize: '60px',
  fontStyle: 'normal',
  fontWeight: '500',
  lineHeight: 'normal',
  position: 'absolute',
  top: '300px',
  left : '750px'
}




let hour = new Date().getHours()
hour = 23
let greet = "null"
if(hour > 0 && hour < 12){
  greet = "Good Morning"
  time.color = 'skyblue'
}
else if(hour >= 12 && hour < 18 ){
  greet = "Good Afternoon"
}
else{
  greet = "Good Night"
  time.color = 'rgb(29, 29, 57)';
}

ReactDOM.render(
  <>
  <div style={Frame}>
    <div className="Block">
      <div className="helo">Hello Sir,</div>
      <div style={time}>{greet}</div>
    </div>
  </div>
  </>,
  document.getElementById("root")
)
