import React, { useState } from "react";


const App = (()=>{

    
    let bg = 'rgb(237, 255, 255)'

    const [bgc,setcol] = useState(bg)
    const bgChange=(()=>{
        bg = 'rgb(192, 221, 197)'
        setcol(bg)
    })

    const oribg = (()=>{
        bg = 'rgb(237, 255, 255)'
        setcol(bg)
    })
    setInterval(oribg,5000)
    return (
    <>
    <div style={{backgroundColor: bgc}} className="divi">
        <button className="but" onClick={bgChange}>Click me</button>
    </div>
    </>
    )
})
export default App
