import React, { useState } from "react";
import AddIcon from '@material-ui/icons/Add';
import AddIcons from '@material-ui/icons/Delete';
import './Project8.css'

const Project8 = (()=>{

    const[count,change] =useState(0)

    const inc = (()=>{
        change(count+1)
    })    
    const dec = (()=>{
        if(count >0){
        change(count-1)
        }
        else{
            alert("Minimum Vlaue of counter Reached ")
        }
    })


    return (<>
       <div className="main" width = "100%">
        <div className="card">
            <div className="heading"><h1>{count}</h1></div>
            <button className="inc" onClick={inc}><AddIcon/></button>
            <button className="dec" onClick={dec}>Decrement</button>
        </div>
       </div>
    </>)
})
export default Project8
